import React, { Component } from 'react';
import axios from 'axios';

class PostMessage extends Component {
  state = {
    title: '',
    message: ''
  };
  render() {
    return (
      <form className="postBlock" onSubmit={this.handleSubmit}>
        <button>{`Post a new ${this.props.typeOfPost}`}</button>
        <input
          onChange={this.handleChange}
          type="text"
          value={this.state.message}
        />
      </form>
    );
  }
  handleSubmit = event => {
    event.preventDefault();
    const id = this.props.id;
    const slug = this.props.slug;
    const content = this.state.message;
    const title = this.state.title;
    if (this.props.typeOfPost === 'article') {
      return axios
        .post(
          `https://northcoders-news-ruimak.herokuapp.com/api/topics/${slug}/articles`,
          {
            body: content,
            title: 'New article',
            votes: 0,
            created_by: '5b9bc4254c18302d443a6330'
          }
        )
        .then(
          this.setState({
            message: '',
            title: ''
          })
        )
        .catch(console.log);
    } else if (this.props.typeOfPost === 'comment') {
      return axios
        .post(
          `https://northcoders-news-ruimak.herokuapp.com/api/articles/${id}/comments`,
          { body: content, created_by: '5b9bc4254c18302d443a6330' }
        )
        .then(comment => {
          console.log(comment);
          this.setState({
            message: ''
          });
        });
    }
  };
  handleChange = event => {
    this.setState({
      message: event.target.value
    });
  };
}

export default PostMessage;
