import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { getAllUsers } from '../api.js';
import { Redirect } from 'react-router';

class Users extends Component {
  state = { users: [], errStatus: null };
  componentDidMount() {
    return getAllUsers().then(users => {
      this.setState({ users: users.data.users }).catch(err => {
        this.setState({ errStatus: err });
      });
    });
  }
  render() {
    if (this.state.errStatus)
      return (
        <Redirect
          to={{
            pathname: '/NotFound',
            state: { message: 'There is no such user. ' }
          }}
        />
      );
    return (
      <div className="displayInfoArea">
        <h1 className="titleWithMarginBottom">Users</h1>
        {this.state.users.map(user => {
          return (
            <div key={user._id} className="singleUserDiv">
              <Link to={`/users/${user.username}`} className="entryTitle">
                <img
                  className="userThumbnail"
                  src={user.avatar_url}
                  alt={`${user.username} avatar`}
                />
                {user.username}
              </Link>
            </div>
          );
        })}
      </div>
    );
  }
}

export default Users;
